DROP DATABASE IF EXISTS `silicone_hotel`;
CREATE DATABASE `silicone_hotel` CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci;
USE `silicone_hotel`;

CREATE TABLE `user` (
  `id` int NOT NULL AUTO_INCREMENT,
  `username` varchar(30) NOT NULL UNIQUE,
  `password` varchar(255) NOT NULL,
  `email` varchar(254),
  `role` ENUM('admin', 'staff') NOT NULL,
  `theme` ENUM('light', 'dark') DEFAULT 'light',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

CREATE TABLE `customer` (
  `id` int NOT NULL AUTO_INCREMENT,
  `first_name` varchar(255) NOT NULL,
  `last_name` varchar(255) NOT NULL,
  `email` varchar(254),
  `phone` varchar(18) NOT NULL,
  `street_address` varchar(255) NOT NULL,
  `city` varchar(255) NOT NULL,
  `zip_code` int NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE(`email`, `phone`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

CREATE TABLE `room` (
  `id` int NOT NULL, 
  `beds` int NOT NULL, 
  `location` varchar(255), 
  `information` varchar(255), 
  `size` int NOT NULL, 
  `in_use` boolean DEFAULT TRUE,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

CREATE TABLE `booking` (
  `id` int NOT NULL AUTO_INCREMENT,
  `customer_id` int,
  `room_id` int NOT NULL,
  `start_date` date NOT NULL,
  `end_date` date NOT NULL,
  `paid` boolean DEFAULT FALSE,
  `payment_method` ENUM('card', 'cash', 'invoice') NOT NULL,
  PRIMARY KEY (`id`),
  FOREIGN KEY (`customer_id`) REFERENCES `customer` (`id`) ON DELETE SET NULL,
  FOREIGN KEY (`room_id`) REFERENCES `room` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

CREATE OR REPLACE VIEW `search` AS
  SELECT
    booking.*,
    customer.first_name,
    customer.last_name,
    customer.email,
    customer.phone,
    customer.street_address,
    customer.zip_code,
    customer.city,
    room.beds,
    room.location,
    room.information,
    room.size,
    room.in_use
	FROM booking, customer, room
	WHERE booking.customer_id = customer.id AND booking.room_id = room.id; 

